import { useState } from 'react';
import './App.css';


function App() {

  // Set Name
  const [name, setName] = useState("");
  // Set Age
  const [age, setAge] = useState();
  // Set File
  const [file, setFile] = useState(new File([], "file"));

  // Function called on submit button
  function OnSubmitClicked() {

    if (name === "" || name == null) {
      alert("Name cannot be empty")
    }
    else if (name.length < 3) {
      alert("Name should contain atleast 3 letters")
    }
    else if (age === "" || age == null) {
      alert("Age cannot be empty")
    }
    else if (age > 125 || age < 1) {
      alert("Age out of bounds")
    }
    else if (file.size > 400000) {
      alert("File size cannot be more than 4MB")
    }
  }

  return (
    <div className="App">
      <div>
        <input placeholder='Your name' value={name} onChange={(e) => { setName(e.target.value) }}></input>
        <input placeholder='Age' type='number' min={1} value={age} onChange={(e) => { setAge(e.target.value) }}></input>
        <input type='file' size={1} value={file} onChange={(e) => { setFile(e.target.value) }}></input>
        <button onClick={() => OnSubmitClicked()}>Submit</button>
      </div>
    </div>
  );
}

export default App;
